import element from "./js/element";
import dom from "./js/dom";
import markup from "./js/markup";


export default {
  query   : dom.query,
  dom     : dom,
  element : element,
  markup  : markup
};

import helpers from "./internal/helpers";

var _each    = helpers.each,
    _uniques = helpers.uniques;

/**
 * Wrap an element in a classlist-like object such that it can add and remove classes
 * in a manner compatible with ie8+ 
 * 
 * @param {Node} element - The element to wrap in a classlist object so it can use `.add()`, `.remove()`
 * etc. 
 * 
 * @return {object} The API that acts entirely on the element provided. 
 */
function classlist(element) {
  var classes = element.className,
      list    = classes.replace(/ +/g, " ").split(" ");
      
  function _contains(cls) {
    return list.indexOf(cls) > -1;
  }

  function _set(cls) {
    element.className = cls.join(" ").trim();

    return element; 
  }

  // TODO: support native classList if available
  function _add(cls) {
    // Passed in classes split into an array
    var individuals = cls.split(" ");

    return _set(_uniques(list.concat(individuals)));
  }

  // Remove classes from an element 
  // TODO: support native classList if available
  function _remove(cls) {
    var individuals = _uniques(cls.split(" "));

    _each(individuals, function(classname) {
      var at; 
      
     if(_contains(classname)) {
        at = list.indexOf(classname);

        list.splice(at, 1);
      }
    });

    _set(list);
  }

  return {
    add    : _add,
    remove : _remove,
    has    : _contains
  };
}

export default classlist;

/**
 * @module DOM
 */
import helpers from "./internal/helpers";

var _elements = helpers.elements,
    _exclude  = helpers.exclude,
    _unwrap   = helpers.unwrap;

/**
 * Finds an element by selector within a context. If child elements in 
 * element match selector, they are added to the return result
 * 
 * @param {Node} element - The element to search for children inside of
 * @param {string} selector - The CSS selector to match elements against
 * 
 * @returns {array|Node} A single element or collection of elements that match the query
 */
export function find(element, selector) {
  return _unwrap(_elements(element.querySelectorAll(selector)));
}

/**
 * Finds an element by selector within the context of context (document.body 
 * if no context is supplied). This is a bare-bones analog of jQuery's `$()`
 * 
 * @param {string} selector - The CSS selector to match elements against
 * @param {Node} context - The element to search inside of
 * 
 * @returns {array|Node} A single element or collection of elements that match the query
 */
export function query(selector, context = document.body) {
  return (selector === "body") ? context : find(context, selector);
}

// find the closest element that matches (includes self)
/**
 * Finds the closest parent element of element that matches selector
 * starting with the element this function was passed
 * 
 * @param {Node} element - The element from which to begin the search
 * @param {string} selector - The CSS selector to match elements against
 * 
 * @returns {Node} The first ancestor element that matches the query
 */
export function closest(element, selector) {
  var current  = element;

  // Keep looking up the DOM if our current element doesn't match, stop at <html>
  while(current.matches && !current.matches(selector) && current.tagName !== "HTML") {
    current = parent(current);
  }

  return (current.matches(selector)) ? current : [];
}

/**
 * Returns the immediate parent of a given node
 * 
 * @param {Node} element - The element from which to return a parent
 * 
 * @return {Node} element - The parent element of the element passed in
 */
export function parent(element) {
  return element.parentNode;
}

/**
 * Returns an array of children of the passed in element. Will return a single element
 * if the array contains only one child. 
 * 
 * @param {Node} element - The element from which to retrieve children
 * 
 * @return {array|Node} element - The children of element
 */
export function children(element) {
  return _unwrap(_elements(element.childNodes));
}

/**
 * Returns siblings of a given node. If a selector is specified, it will
 * return only the siblings that match that selector. 
 * 
 * @param {Node} element - The element from which to return a parent
 * @param {string} selector - the selector to match elements against
 * 
 * @return {array|Node} element - The sibling(s) of the element passed in. 
 */
export function siblings(element, selector) {
  var result = selector ? _exclude(find(parent(element), selector), element) :
                          _exclude(children(parent(element)), element);
  
  return _unwrap(result);
}

export default {
  query, 
  find,
  closest,
  parent,
  children,
  siblings
};

/**
 * @module element
 */
import classlist from "./classlist";
import dom from "./dom";

/**
 * a getter/setter function that modifies attributes on elements. 
 * 
 * @param {Node} element - The element to set attributes on
 * @param {string} attribute - The attribute to either retrieve or replace with a value
 * @param {string} [val] - The value to set, if any
 * 
 * @returns undefined
 */
export function attr(element, attribute, val) {
  var setting = Object.prototype.toString.call(val) !== "[object Undefined]";

  return setting ? element.setAttribute(attribute, val) : 
                   element.getAttribute(attribute);
}

/**
 * a function to remove attributes on elements
 * 
 * @param {Node} element - The element to remove attributes from
 * @param {string} attribute - The attribute on the element to remove
 * 
 * @returns undefined
 */
export function removeAttr(element, attribute) {
  return element.removeAttribute(attribute);
}

/**
 * a function to retrieve or set the value of a form element. 
 * 
 * @param {Node} element - The element on which to retrieve or set the value
 * @param {string|boolean|number} [val] - The value to set (or retrieve) as the elements value 
 * 
 * @returns {number|string} The value of the element
 */
export function value(element, val) {
  element.value = (val || element.value);

  return element.value; 
}

/**
 * A function that adds a class to the element provided using a 
 * polyfill wrapper for Element.classlist
 * @see classList
 * 
 * @param {Node} element - The element on which to add one (or many, space separated) class(es)
 * @param {string} classes - The classes to add to the element
 * 
 * @return {Node} The element on which the class was set
 */
export function addClass(element, classes) {
  classlist(element).add(classes);
}

/**
 * A function that removes a class from the element provided using a
 * polyfill wrapper for Element.classlist
 * @see classList
 * 
 * @param {Node} element - The element on which to remove one (or many, space separated) class(es)
 * @param {string} classes - The classes to remove from the element
 * 
 * @return {Node} The element on which the class was set
 */
export function removeClass(element, classes) {
  classlist(element).remove(classes);
}

/**
 * A function to set inline CSS properties onto an element
 * @see classList
 * 
 * @example
 * var div = document.createElement("div");
 * 
 * css(div, {
 *   background: blue,
 *   fontSize: "18px"
 * });
 * 
 * //The div will now have inline styles of:
 * // - font-size: 18px;
 * // - background: blue; 
 * 
 * @param {Node} element - The element on which to remove one (or many, space separated) class(es)
 * @param {object} styles - The styles, in camel-case (if applicable), to set on the element
 * 
 * @return {Node} The element on which the class was set
 */
export function css(element, styles) {
  var rule,
      val; 

  for(rule in styles) {
    val = styles[rule];
    element.style[rule] = val; 
  }

  return element; 
}

/**
 * Append a child node to the end of an element's children
 * 
 * @param {Node} element - The element to append a child to
 * @param {Node} appendee - The element that will be moved or appended to the 
 * new parent element
 * 
 * @return {Node} The element that was appended to the parent
 */
export function append(element, appendee) {
  return element.appendChild(appendee);
}

/**
 * Append a child node to the beginning of an element's children
 * 
 * @param {Node} element - The element to prepend a child to
 * @param {Node} prependee - The element that will be moved or prepended to the 
 * new parent element
 * 
 * @return {Node} The element that was prepended to the parent
 */
export function prepend(element, prependee) {
  return element.insertBefore(prependee, element.firstChild);
}

/**
 * Utility to empty elements child contents completely via `innerHTML`
 * 
 * @param {Node} element - The element to empty
 * 
 * @return {Node} The newly emptied element. 
 */
export function empty(element) {
  element.innerHTML = ""; 

  return element; 
}

/**
 * Clones a node, optionally deep clones the node to also clone its children
 * 
 * @param {Node} element - The element to clone
 * @param {boolean} [deep=false] - The flag to determine whether or not the copy is deep 
 * 
 * @return The newly cloned node
 */
export function clone(element, deep) {
  return element.cloneNode(deep); 
}

/**
 * Removes a node from the DOM
 * 
 * @param {Node} element - The element to remove from the DOM. 
 * 
 * @return {Node} The newly removed element
 */
export function remove(element) {
  return dom.parent(element).removeChild(element);
}

export default {
  attr,
  removeAttr,
  value,
  addClass,
  removeClass,
  css,
  append,
  prepend,
  empty, 
  clone,
  remove
};

